import "./App.css";

import React from "react";
import Navbar from "./components/Navbar";
import TextForm from "./components/TextForm";
import About from "./components/About";
import { useState } from "react";
import Alert from "./components/Alert";

import { Routes, Route, BrowserRouter } from "react-router-dom";

// import { Routes, Route } from "react-router-dom";
function App() {
  const [mode, setMode] = useState("light"); //whether dark mood unabled or not
  const [alert, setAlert] = useState(null);
  const showAlert = (message, type) => {
    setAlert({
      msg: message,
      type: type,
    });

    setTimeout(() => setAlert(null), 1000);
  };

  const removeClasses = () => {
    document.body.remove.classList.remove("bg-light");
    document.body.remove.classList.remove("bg-dark");
    document.body.remove.classList.remove("bg-success");
    document.body.remove.classList.remove("bg-warning");
    document.body.remove.classList.remove("bg-danger");
  };

  const toggleMode2 = (cls) => {
    removeClasses();
    document.body.classList.add("bg-" + cls);
  };
  const toggleMode = () => {
    removeClasses();

    if (mode === "dark") {
      setMode("light");
      document.body.style.backgroundColor = "white";
      showAlert("Light mood has been enabled", "success");
    } else {
      setMode("dark");
      document.body.style.backgroundColor = "#1e0b69";
      showAlert("Dark mood has been enabled", "success");
    }
  };
  return (
    <BrowserRouter>
      <Navbar
        title="TextUtils"
        aboutText="About"
        mode={mode}
        toggle={toggleMode}
        toggle={toggleMode2}
      />
      <Alert alert={alert} />
      <Routes>
        <Route
          path="/"
          element={
            <TextForm
              heading="Text to test"
              mode={mode}
              Showalert={showAlert}
            />
          }
        />
        <Route path="/about" element={<About mode={mode} />} />
      </Routes>
      <footer id="customFooter">Coded by Qazi Saboor</footer>
    </BrowserRouter>
  );
}

export default App;
