import "./App.css";

import React from "react";
import Navbar from "./components/Navbar";
import TextForm from "./components/TextForm";
import About from "./components/About";
import { useState } from "react";
import Alert from "./components/Alert";

import Test from "./Test";
import { Routes, Route, Router } from "react-router-dom";

// import { Routes, Route } from "react-router-dom";
function App() {
  const [mode, setMode] = useState("light"); //whether dark mood unabled or not
  const [alert, setAlert] = useState(null);
  const showAlert = (message, type) => {
    setAlert({
      msg: message,
      type: type,
    });

    setTimeout(() => setAlert(null), 1000);
  };
  const toggleMode = () => {
    if (mode === "dark") {
      setMode("light");
      document.body.style.backgroundColor = "white";
      showAlert("Light mood has been enabled", "success");
    } else {
      setMode("dark");
      document.body.style.backgroundColor = "#1e0b69";
      showAlert("Dark mood has been enabled", "success");
    }
  };
  return (
    <Router>
      <div>
        <Navbar
          title="TextUtils"
          aboutText="About"
          mode={mode}
          toggle={toggleMode}
        />
        {/* Render the Navbar component */}
        <Routes>
          {/* Define routes */}
          <Route
            path="/"
            element={
              <TextForm
                heading="Text to test"
                mode={mode}
                Showalert={showAlert}
              />
            }
          />
          <Route path="/about" element={<About />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;

